import React, { useState, useEffect } from 'react';
import './App.css';
import PDFReader from './Components/PDFReader';
import PDFList from './Components/PDFList';
import Search from './Components/Search';

function App() {
  const [pdfs, setPdfs] = useState([]);
  const [searchQuery, setSearchQuery] = useState('');
  const [selectedPdf, setSelectedPdf] = useState(null);

  // Fetch PDF data from the API
  useEffect(() => {
    fetch('https://api.npoint.io/dee51ea017d20efdfcc8')
      .then((response) => response.json())
      .then(setPdfs)
      .catch((error) => console.error('Error fetching PDFs:', error));
  }, []);

  // Filter PDFs by title or author
  const filteredPdfs = pdfs.filter((pdf) =>
    (pdf.author).toLowerCase().includes(searchQuery.toLowerCase())
  );

  return (
    <div className="App">
      <h1>PDF Browser</h1>

      {/* Use the Search component here */}
      <Search searchQuery={searchQuery} setSearchQuery={setSearchQuery} />

      {selectedPdf ? (
        <PDFReader pdf={selectedPdf} setSelectedPdf={setSelectedPdf} />
      ) : (
        <PDFList
          pdfs={filteredPdfs}
          setSelectedPdf={setSelectedPdf}
        />
      )}
    </div>
  );
}

export default App;
