import React from "react";

function PDFList({ pdfs, setSelectedPdf }) {
  return (
    <div className="pdf-list">
      {pdfs.length === 0 ? (
        <p>No PDFs found.</p> //if there is no pdfs
      ) : (
        pdfs.map((pdf) => (
          <div key={pdf.id} className="pdf-item" onClick={() => setSelectedPdf(pdf)} // Click handler to select the PDF
            role="button" // Role of the element is a button
            
            aria-label={`Select PDF titled: ${pdf.title}`} // Accessibility improvement
            >
          
            <div className="pdf-info">
              <h3>{pdf.name}</h3>
              <p>{"by"+ pdf.author}</p>
              <p>{pdf .thumbnail}</p>
              <p>{pdf .published}</p>
              <a
                href={pdf.link} // Link to open
                
              >
                <button>Open PDF</button> {/* Button styled as a link */}
              </a>
            </div>
          </div>
        ))
      )}
    </div>
  );
}

export default PDFList;
