import React from "react";

function PDFList({ pdfs, setSelectedPd }) {
  // This function will be called when a PDF item is clicked
  const handlePdfClick = (pdfUrl) => {
    // Open the link in a new tab
    window.open(pdfUrl, "_blank");
  };

  return (
    <div className="pdf-list">
      {pdfs.map((pdf) => (
        <div
          key={pdf.id}
          className="pdf-item"
          onClick={() => handlePdfClick(pdf.pdfUrl)} // Open the PDF link when clicked
        >
          <img src={pdf.thumbnail} alt={pdf.title} className="pdf-thumbnail" />
          <div className="pdf-info">
            <h3>{pdf.title}</h3>
            <p>{pdf.author}</p>
          </div>
        </div>
      ))}
    </div>
  );
}

export default PDFList;
