import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
  const [pdfs, setPdfs] = useState([]);
  const [searchQuery, setSearchQuery] = useState('');
  const [selectedPdf, setSelectedPdf] = useState(null);

  // Fetch PDF data from the API
  useEffect(() => {
    fetch('https://api.npoint.io/dee51ea017d20efdfcc8')
      .then((response) => response.json())
      .then((data) => setPdfs(data))
      .catch((error) => console.error('Error fetching PDFs:', error));
  }, []);

  // Filter PDFs based on the search query
  const filteredPdfs = pdfs.filter((pdf) =>
    pdf.title.toLowerCase().includes(searchQuery.toLowerCase())
  );

  return (
    <div className="App">
      <h1>PDF Browser</h1>
      <input
        type="text"
        placeholder="Search PDFs..."
        value={searchQuery}
        onChange={(e) => setSearchQuery(e.target.value)}
        className="search-bar"
      />

      {selectedPdf ? (
        <div className="pdf-reader">
          <h2>{selectedPdf.title}</h2>
          <button onClick={() => setSelectedPdf(null)}>Back to List</button>
          <iframe src={selectedPdf.link} width="100%" height="600px" title={selectedPdf.title}></iframe>
        </div>
      ) : (
        <div className="pdf-list">
          {filteredPdfs.length === 0 ? (
            <p>No PDFs found</p>
          ) : (
            filteredPdfs.map((pdf) => (
              <div
                key={pdf.id}
                className="pdf-item"
                onClick={() => setSelectedPdf(pdf)} // Select PDF when clicked
              >
                <img
                  src={pdf.thumbnail || '/fallback-image.jpg'}
                  alt={pdf.title}
                  className="pdf-thumbnail"
                />
                <h3>{pdf.title}</h3>
                <p>{pdf.author}</p>
                <button onClick={() => window.open(pdf.link, '_blank')}>Open PDF</button>
              </div>
            ))
          )}
        </div>
      )}
    </div>
  );
}

export default App;
