import React from "react";

function PDFList({ pdf=[], setSelectedPdf }) 
{
  if(pdfs.length===0) 
    {
       <p>No PDFs found.</p>; //if pdf not found 
      }

  return (
    <div className="pdf-list">
      {pdfs.map(({ id, name, author, thumbnail, published, link }) => (
        <div
          key={id}
          className="pdf-item"
          onClick={() => setSelectedPdf({ id, name, author, thumbnail, published, link })}
          role="button">


          <h3>{name}</h3>
          <p>{author}</p>
          <p>{thumbnail}</p>
          <p>{published}</p>
          <a href={link}>
            <button>Open PDF</button>
          </a>
        </div>
      ))}
    </div>
  );
}


export default PDFList;
