import React from "react";

function PDFList({ pdfs = [] }) {
  if (pdfs.length === 0) {
    return <p>No PDFs found.</p>;
  }

  return (
    <div className="pdf-list">
      
        <div
          key={id}
          className="pdf-item"
          onClick={() => setSelectedPdf({ id, name, author, thumbnail, published, link })}
          role="button"
          aria-label={`Select PDF name: ${name}`}
        >
          <h3>{name}</h3>
          <p>{author}</p>
          <p>{thumbnail}</p>
          <p>{published}</p>
          <a href={link}>
            <button>Open PDF</button>
          </a>
        </div>
      ))}
    </div>
  );
}



export default PDFList;
