import React, { useState, useEffect } from 'react';
import './App.css';
import PDFReader from './Components/PDFReader';
import PDFList from './Components/PDFList';

function App() {
  const [pdfs, setPdfs] = useState([]);
  const [searchQuery, setSearchQuery] = useState('');
  const [selectedPdf, setSelectedPdf] = useState(null);

  // Fetch PDF data from the API
  useEffect(() => {
    fetch('https://api.npoint.io/dee51ea017d20efdfcc8')
      .then((response) => response.json())
      .then((data) => setPdfs(data));
  }, []);

  // Handle search filter
  const filteredPdfs = pdfs.filter( (pdf) =>
    (pdf.title || '').toLowerCase().includes(searchQuery.toLowerCase())
  );

  // Handle selecting a PDF
  const handleSelectPdf = (pdf) => {
    setSelectedPdf(pdf);
  };

  // If selectedPdf is not in filtered list, reset it
  useEffect(() => {
    if (selectedPdf && !filteredPdfs.includes(selectedPdf)) {
      setSelectedPdf(null);
    }
  }, [filteredPdfs, selectedPdf]);

  return (
    <div className="App">
      <h1>PDF Browser</h1>
      <input
        type="text"
        placeholder="Search PDFs..."
        value={searchQuery}
        onChange={(e) => setSearchQuery(e.target.value)}
        className="search-bar"
      />

      {selectedPdf ? (
        <PDFReader pdf={selectedPdf} setSelectedPdf={setSelectedPdf} />
      ) : (
        <PDFList
          pdfs={filteredPdfs}
          setSelectedPdf={handleSelectPdf} // Use the new handler for selection
        />
      )}
    </div>
  );
}

export default App;
