import React from "react";

function PDFList({ pdfs }) {
  if (pdfs.length === 0) {
    return <p>No PDFs found.</p>;
  }

  return (
    <div className="pdf-list">
      {pdfs.map(({id, name, author, thumbnail, published, link }, index)  (
        <div className="pdf-item" key={index}>
          <h3>{name}</h3>
          <p>{author}</p>
          {/* If thumbnail is an image URL, display it */}
          {thumbnail && <img src={thumbnail} alt={name} />}
          <p>{published}</p>
          <a href={link} target="_blank" rel="noopener noreferrer">
            <button>Open PDF</button>
          </a>
        </div>
      ))}
    </div>
  );
}

export default PDFList;
